mapper.select:
SELECT article_id, title, redirect_url, content, comment_number, status, create_date, update_date, article_cate_id FROM article_tb
AND article_cate_id=#{articleCateId}
AND DATE(create_date)=DATE(#{createDate})
AND DATE(update_date)=DATE(#{updateDate})
AND status=#{status}
WHERE ${orderName}
>=
<=
(SELECT ${orderName} FROM Article_tb ORDER BY
AND article_cate_id=#{articleCateId}
AND DATE(create_date)=DATE(#{createDate})
AND DATE(update_date)=DATE(#{updateDate})
AND status=#{status}
${orderName} ${orderWay} LIMIT #{pageNum}, 1 )
ORDER BY ${orderName} ${orderWay} LIMIT #{pageSize}

SELECT count(article_id) FROM article_tb
AND article_cate_id=#{articleCateId}
AND DATE(create_date)=DATE(#{createDate})
AND DATE(update_date)=DATE(#{updateDate})
AND status=#{status}
