mapper.select:

        SELECT
	     finance_record_id,
	 	 method,
   		 type,
   		 transaction_number,
   		 money,
   		 status,
   		 create_date,
   		 update_date,
   		 account_id
	FROM finance_record_tb


		AND  account_id=#{accountId}


    	AND status=#{status}


    	AND method=#{method}


    	AND type=#{type}


    	AND transaction_number=#{transactionNumber}


		AND  DATE(create_date)=DATE(#{createDate})


		AND  DATE(update_date)=DATE(#{updateDate})

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM finance_record_tb 


		AND  account_id=#{accountId}


    	AND status=#{status}


    	AND method=#{method}


    	AND type=#{type}


    	AND transaction_number=#{transactionNumber}


		AND  DATE(create_date)=DATE(#{createDate})


		AND  DATE(update_date)=DATE(#{updateDate})


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


	SELECT
	 	count(finance_record_id)
	FROM finance_record_tb


		AND  account_id=#{accountId}


    	AND status=#{status}


    	AND method=#{method}


    	AND type=#{type}


    	AND transaction_number=#{transactionNumber}


		AND  DATE(create_date)=DATE(#{createDate})


		AND  DATE(update_date)=DATE(#{updateDate})
