mapper.select:

        SELECT
	     integral_board_id,
	 	 type,
   		 time_type,
   		 integral,
   		 record_time,
   		 create_date,
   		 update_date,
   		 account_id
	FROM integral_board_tb


		AND  type=#{type}


		AND  time_type=#{timeType}


		AND  account_id=#{accountId}


		AND  DATE(create_date)=DATE(#{createDate})


		AND  DATE(update_date)=DATE(#{updateDate})

	WHERE ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM integral_board_tb 
		ORDER BY 


		AND  type=#{type}


		AND  time_type=#{timeType}


		AND  account_id=#{accountId}


		AND  DATE(create_date)=DATE(#{createDate})


		AND  DATE(update_date)=DATE(#{updateDate})


			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(integral_board_id)
FROM integral_board_tb


	AND  type=#{type}


	AND  time_type=#{timeType}


	AND  account_id=#{accountId}


	AND  DATE(create_date)=DATE(#{createDate})


	AND  DATE(update_date)=DATE(#{updateDate})
