mapper.select:

 SELECT
	    notice_id,
	    region,
	 	title,
	 	img_address,
    	content,
    	status,
    	create_date,
    	update_date,
    	account_id,
    	parent_id
	FROM notice_tb


		AND  region=#{region}


		AND  title=#{title}


		AND  status=#{status}


		AND account_id=#{accountId}


		AND parent_id=#{parentId}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM notice_tb 


		AND  region=#{region}


		AND  title=#{title}


		AND  status=#{status}


		AND account_id=#{accountId}


		AND parent_id=#{parentId}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(notice_id)
FROM notice_tb


	AND  region=#{region}


	AND  title=#{title}


	AND  status=#{status}


	AND  account_id=#{accountId}


	AND parent_id=#{parentId}
