mapper.select:

 SELECT
	    notice_id,
	 	title,
	 	img_address,
    	content,
    	status,
    	create_date,
    	update_date,
    	account_id
	FROM notice_tb


		AND  title=#{title}


		AND  status=#{status}


		AND ( account_id=#{accountId}
		OR ISNULL(account_id))

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM notice_tb 


		AND  title=#{title}


		AND  status=#{status}


		AND ( account_id=#{accountId}
		OR ISNULL(account_id))


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(notice_id)
FROM notice_tb


	AND  title=#{title}


	AND  status=#{status}


	AND  account_id=#{accountId}
