mapper.select:
SELECT order_detail_id, name, img_address, total_price, number, create_date, update_date, business_id, order_id FROM order_detail_tb
AND order_id=#{orderId}
AND DATE(create_date)=DATE(#{createDate})
AND DATE(update_date)=DATE(#{updateDate})
AND ${orderName}
>=
<=
(SELECT ${orderName} FROM order_detail_tb
AND order_id=#{orderId}
AND DATE(create_date)=DATE(#{createDate})
AND DATE(update_date)=DATE(#{updateDate})
order BY ${orderName} ${orderWay} LIMIT #{pageNum}, 1 )
order BY ${orderName} ${orderWay} LIMIT #{pageSize}
