mapper.select:
SELECT split_id, realname, remark, number, price, apply_date, split_date, create_date, update_date, status, account_id, buy_account_id FROM split_tb
AND account_id=#{accountId}
AND buy_account_id=#{buyAccountId}
AND DATE(apply_date)=DATE(#{applyDate})
AND DATE(split_date)=DATE(#{splitDate})
AND DATE(create_date)=DATE(#{createDate})
AND DATE(update_date)=DATE(#{updateDate})
AND status=#{status}
AND ${orderName}
>=
<=
(SELECT ${orderName} FROM split_tb
AND account_id=#{accountId}
AND buy_account_id=#{buyAccountId}
AND DATE(apply_date)=DATE(#{applyDate})
AND DATE(split_date)=DATE(#{splitDate})
AND DATE(create_date)=DATE(#{createDate})
AND DATE(update_date)=DATE(#{updateDate})
AND status=#{status}
ORDER BY ${orderName} ${orderWay} LIMIT #{pageNum}, 1 )
ORDER BY ${orderName} ${orderWay} LIMIT #{pageSize}


SELECT
 	count(split_id)
FROM split_tb


	AND  account_id=#{accountId}


	AND  buy_account_id=#{buyAccountId}


	AND  DATE(apply_date)=DATE(#{applyDate})


	AND  DATE(split_date)=DATE(#{splitDate})


	AND  DATE(create_date)=DATE(#{createDate})


	AND  DATE(update_date)=DATE(#{updateDate})


	AND  status=#{status}
